import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import HomePage from "./pages/HomePage"
import ProjectsPage from "./pages/ProjectsPage";
import 'bootstrap/dist/css/bootstrap.min.css';
import ContactPage from "./pages/ContactPage";
import AboutPage from "./pages/AboutPage";
import HeaderComponent from "./Components/HeaderComponent";
import { useState } from "react";
import NavbarComponent from "./Components/NavbarComponent";

function App() {

  const [Color, setColor] = useState("light")

  const change =()=>{
    if (Color==="dark") {
      setColor("light");
      document.body.style.backgroundColor = "white"

    } else {
      setColor("dark");
      document.body.style.backgroundColor = "#212529"

    }
  }
  return (
    <Router>
    <NavbarComponent change = {change}/>
      <Routes>
        <Route exect path="/" element={<HomePage Color = {Color}/>}>
        </Route>
        <Route exect path="/projects" element={<ProjectsPage/>}>
        </Route>
        <Route exect path="/contact" element={<ContactPage/>}>
        </Route>
        <Route exect path="/about" element={<AboutPage/>}>
        </Route>
      </Routes>
    </Router>

  );
}

export default App;
